import { Cell } from "./Cell";
import { Colors } from "./Colors";
import { Board } from "./Board";
import { Player } from "./Player";
import { Figure } from "./figures/Figure";

export class CheckMateChecker {
  board: Board;
  cell: Cell;
  cells: Cell[][] = [];
  constructor(cell: Cell, board: Board) {
    this.board = board;
    this.cell = cell;
  }

  checkMate(selectedCell: Cell | null) {
    for (let i = 0; i < 8; i++) {
      const row = this.cells[i];
      for (let j = 0; j < 8; j++) {
        const target = row[j];
        if (
          selectedCell?.figure &&
          !this.board.isAvailableMove(selectedCell.figure, selectedCell, target)
        ) {
          console.log("checkMate");
          return true;
        }
        return false;
      }
    }
  }
}
