import { Cell } from "./Cell";
import { Colors } from "./Colors";
import { Board } from "./Board";
import { Player } from "./Player";
import { Figure } from "./figures/Figure";

export function isCheckMate(
  selectedCell: Cell | null,
  board: Board,
  cells: Cell[][] = [],
): boolean {
  console.log(board);
  console.log(board.cells[0]);
  for (let i = 0; i < 8; i++) {
    const row = cells[i];
    for (let j = 0; j < 8; j++) {
      console.log(row);
      const target = row[j];
      if (
        selectedCell?.figure &&
        board.isAvailableMove(selectedCell.figure, selectedCell, target)
      ) {
        return false;
      }
    }
  }
  return true;
}
