import React,{ FC} from 'react';
import cl from "./endOfTheGame.module.css";
import { Colors } from "../../models/Colors";
import { Player } from "../../models/Player";

interface endOfTheGameProps {
    visible: boolean;
    setVisible: any;
    currentPlayer: Player | null;
    restart: () => void;
  }

const EndOfTheGame: FC<endOfTheGameProps> =({ restart, visible, setVisible, currentPlayer}) => {

  const rootClasses = [cl.myModal]
  let winner;
  whoIsAWinner(currentPlayer);

  if (visible) {
    rootClasses.push(cl.active)
  }

  function whoIsAWinner(currentPlayer : Player | null) {
  if (currentPlayer?.color === Colors.WHITE) { 
    winner = "Белый игрок"
  } else {
     winner = "Черный игрок"
    }
  }
  return (
    <div className={rootClasses.join(' ')} onClick ={() => setVisible(false)}>
      <div className={cl.myModalContent} onClick={(e)=>{e.stopPropagation()}}>
        {winner} победил!
        <button className="c-button" onClick={restart}> Новая игра</button>
      </div>
    </div>
  );
};

export default EndOfTheGame;
