import { Cell } from "./Cell";
import { Colors } from "./Colors";
import { Board } from "./Board";
import { Player } from "./Player";
import { Figure } from "./figures/Figure";

export function isCheckMate(selectedCell: Cell | null, board: Board): boolean {

  for (let i = 0; i < 8; i++) {
    for (let j = 0; j < 8; j++) {
      const target = board.getCell(j, i);
      if (
        selectedCell?.figure &&
        board.isAvailableMove(selectedCell.figure, selectedCell, target)
      ) {
        return false;
      }
    }
  }
  return true;
}
