import React,{ FC} from 'react';
import cl from "./endOfTheGame.module.css";
import { Colors } from "../../models/Colors";

interface endOfTheGameProps {
    visible: boolean;
    setVisible: any;
    currentPlayer: any;
  }

const EndOfTheGame: FC<endOfTheGameProps> =({ visible, setVisible, currentPlayer}) => {

  const rootClasses = [cl.myModal]

  if (visible) {
    rootClasses.push(cl.active)
  }

  const winner = (currentPlayer): string =>{
  if (currentPlayer?.color === Colors.WHITE) { return "Белый игрок"} 
  else ("Черный игрок")
  }
  return (
    <div className={rootClasses.join(' ')} onClick ={() => setVisible(false)}>
      <div className={cl.myModalContent} onClick={(e)=>{e.stopPropagation()}}>
        {winner} 
        <button className="c-button"> Новая игра</button>
      </div>
    </div>
  );
};

export default EndOfTheGame;
