import React, { useEffect, useState, useRef } from "react";
import { Board } from "./models/Board";
import BoardComponent from "./components/BoardComponent";
import LostFigures from "./components/LostFigures";
import EndOfTheGame from "./components/endOfTheGame/EndOfTheGame";
import Timer from "./components/Timer";
import "./App.css";
import { Player } from "./models/Player";
import { Colors } from "./models/Colors";

const App = () => {
  const [board, setBoard] = useState(new Board());
  const [whitePlayer, setWhitePlayer] = useState(new Player(Colors.WHITE));
  const [blackPlayer, setBlackPlayer] = useState(new Player(Colors.BLACK));
  const [currentPlayer, setCurrentPlayer] = useState<Player | null>(null);

  const [modal, setModal] = useState(false);

  useEffect(() => {
    restart();
  }, []);

  function restart() {
    const newBoard = new Board();
    newBoard.initCells();
    newBoard.addFigures();
    setBoard(newBoard);
    setCurrentPlayer(whitePlayer);
  }

  function swapPlayer() {
    if (currentPlayer?.color === Colors.WHITE) {
      setCurrentPlayer(blackPlayer);
    } else {
      setCurrentPlayer(whitePlayer);
    }
  }

  const createEndGameForm = () => {
    setModal(true);
  };

  return (
    <div className="app">
      <Timer currentPlayer={currentPlayer} restart={restart} />
      <BoardComponent
        board={board}
        setBoard={setBoard}
        currentPlayer={currentPlayer}
        swapPlayer={swapPlayer}
      />
      <div>
        <button className="c-button" onClick={() => createEndGameForm()}>
          Конец
        </button>
        <EndOfTheGame visible={modal} setVisible={setModal} currentPlayer= {currentPlayer}></EndOfTheGame>
      </div>
      <div>
        <LostFigures title="Черные фигуры:" figures={board.lostBlackFigures} />
        <LostFigures title="Белые фигуры:" figures={board.lostWhiteFigures} />
      </div>
    </div>
  );
};

export default App;
